document.addEventListener("DOMContentLoaded",function(){const e=document.getElementById("header");e&&fetch("/components/header.html").then(e=>e.text()).then(n=>{e.innerHTML=n;const t=document.documentElement.getAttribute("data-theme")||(window.matchMedia("(prefers-color-scheme: dark)").matches?"dark":"light");updateThemeToggleIcon(t),setActiveNavigation()}).catch(n=>{console.error("Error loading header:",n),e.innerHTML='\n                    <nav class="navigation">\n                        <div class="nav-container">\n                            <div class="brand">\n                                <a href="/" class="logo">Iris</a>\n                            </div>\n                            <div class="nav-items">\n                                <a href="/" class="nav-item">Home</a>\n                                <a href="/waitlist" class="nav-item">Waitlist</a>\n                            </div>\n                        </div>\n                    </nav>\n                '});const n=document.getElementById("footer");n&&fetch("/components/footer.html").then(e=>e.text()).then(e=>{n.innerHTML=e}).catch(e=>{console.error("Error loading footer:",e),n.innerHTML='\n                    <footer class="footer">\n                        <div class="footer-container">\n                            <div class="footer-section">\n                                <p>&copy; 2025 Iris. All rights reserved.</p>\n                            </div>\n                        </div>\n                    </footer>\n                '})});